const CardListContainer = () =>{

  const { cards } = useContext(CardContext) 

 
  
  return(
      
      cards.length > 0? 
      <CardList>
        {
        cards.map((cards, i) =>(            
              <Card 
              key={i}
              id={cards.id}
              name={cards.name}
              numberOfCreditCard={cards.numberOfCreditCard}
              until= {cards.until}
              creditCard= {cards.creditCard}
              img={cards.img}
              kindOfCreditCard={cards.kindOfCreditCard}
              // deleteFunction={()=> deleteFunction(cards.id)}
              keyOfSecurity = {cards.keyOfSecurity}
              
              
              />
                
         ))
        }
      </CardList>
      : <h2> No tienes tarjetas a√∫n, agrega una para simplificar tus pagos</h2>
    
  )
}

export { CardListContainer }